import { Component } from '@angular/core';
import { RoleService } from '../service/role.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-add-role',
  templateUrl: './add-role.component.html',
  styleUrls: ['./add-role.component.scss']
})
export class AddRoleComponent {


  @Output() cancel : EventEmitter<Event>;
  @Output() onSubmitData: EventEmitter<Event>

  public userForm: FormGroup;
  public departments: product[];
  public categoryList!:category[];
  public subCategorylist!:subCategory[];


  constructor(private fb: FormBuilder, private usersService: RoleService, private router: Router, private route: ActivatedRoute,) {
    this.userForm = this.buildUsersForm();
    this.departments = [];
    this.id = this.route.snapshot.params['id'];
    this.isAddMode = !this.id;




  }


  //On init get department list and ckeck if its addMode
  ngOnInit(): void {
    this.getDepartmentList();
    this.getcategoryList();



    this.id = this.route.snapshot.params['id'];
    this.isAddMode = !this.id;

    if (!this.isAddMode) {
      this.usersService.getById(this.id).subscribe(x => this.userForm.patchValue(x));
    }
  }

  //Reactive Form
  private buildUsersForm(): FormGroup {
    return this.fb.group({
      productname: [1, Validators.required],
      category:[1,Validators.required],
      Subcategory:[1,Validators.required],
      Brand:[1, Validators.required],
      Unit:[1, Validators.required],
      Sku:[null,Validators.required],
      MinimumQTY:[null, Validators.required],
      Qty:[null, Validators.required],
      Description:[null,Validators.required],
      Tax:[1 , Validators.required],
      Discount:[1, Validators.required],
      Price:[null,Validators.required],
      Status:[1, Validators.required],
      image:[null],
    });
  }


  //get department list from db
  private getDepartmentList(): void {
    this.usersService.getDepartments().subscribe({
      next:(data: any[]) => {
        this.departments = data;
      },
      error:(e) => console.log(e)
    });
  }

  //category list
  private getcategoryList(): void {
    this.usersService.getCategory().subscribe({
      next:(data: any[]) => {
        this.categoryList = data;
      },
      error:(e) => console.log(e)
    });
  }


    private getStatusList(): void {
      this.usersService.getStatus().subscribe({
        next:(data: any[]) => {
          this.status = data;
        },
        error:(e) => console.log(e)
      });
    }


//for image
public handleInputChange(e: any) {
  var file = e.dataTransfer ? e.dataTransfer.files[0] : e.target.files[0];
  var pattern = /image-*/;
  var reader = new FileReader();
  if (!file.type.match(pattern)) {
    alert('invalid format');
    return;
  }
  reader.onload = this.handleReaderLoaded.bind(this);
  reader.readAsDataURL(file);
}
handleReaderLoaded(e: any) {
  let reader = e.target;
  this.base64Image = reader.result;
  //console.log(this.base64Image);
}

     //Post data to db
  public createProduct(): void {

    this.productData = {
      productname: this.userForm.value.productname,
      category: this.userForm.value.category,
      Subcategory:this.userForm.value.Subcategory,
      Brand:this.userForm.value.Brand,
      Unit:this.userForm.value.Unit,
      Sku:this.userForm.value.Sku,
      MinimumQTY:this.userForm.value.MinimumQTY,
      Qty:this.userForm.value.Qty,
      Description:this.userForm.value.Description,
      Tax:this.userForm.value.Tax,
      Discount:this.userForm.value.Discount,
      Price:this.userForm.value.Price,
      Status:this.userForm.value.Status,
      image: this.base64Image,
    }

    console.log(this.productData, 'dataaaaaaaa');

    this.usersService.createProduct(this.productData).subscribe({
      next:() => {
        this.onSubmitData.emit(this.productData);
        this.navigateToList();
      },
      error:(e:any) => console.log(e)
    });
  }

  //on Form submit
  //on Form submit
  public onSubmit(): void {
    console.log('hello there')
    if (this.isAddMode) {
      this.createProduct();
    }
    else {
      this.updateUser();
    }
  }

   //Put data to db
   public updateUser(): void {

    this.usersService.updateProduct(this.id, this.userForm.value).subscribe({
      next: () => {

        this.navigateToList();
      },
      error: (e:any) => console.log(e)
    })
  }


  public navigateToList(): void {
    this.router.navigate(['/inventory'])
  }

  //Rest to form controls
  onRest() {
    this.userForm.reset();
  }

  onCancel(){
    this.cancel.emit();
  }



}
